
CREATE TABLE materias_referencia_cruzada (
    id_referencia INT AUTO_INCREMENT PRIMARY KEY,
    plan VARCHAR(10),
    clave_materia VARCHAR(10),
    nombre VARCHAR(255),
    clave_materia_ref_cruzada VARCHAR(10),
    nombre_materia_ref_cruzada VARCHAR(255),
    nivel_clase INT,
    creditos INT,
    horas_contacto_semana DECIMAL(4,2),
    escuela VARCHAR(100),
    departamento VARCHAR(100),
    curriculum VARCHAR(50)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

